import arapointLogo from "@assets/generated_images/arapoint_logo_-_security_shield_with_checkmark.png";

export default function Signup() {
  const [_, setLocation] = useLocation();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [step, setStep] = useState<"details" | "otp">("details");
  const [showPassword, setShowPassword] = useState(false);

  const handleSignup = (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    // Simulate API call
    setTimeout(() => {
      setIsLoading(false);
      setStep("otp");
      toast({
        title: "Verification Code Sent",
        description: "Please check your email for the OTP.",
      });
    }, 1500);
  };

  const handleVerify = (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      toast({
        title: "Account Created",
        description: "Welcome to Arapoint!",
      });
      setLocation("/dashboard");
    }, 1500);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-muted/30 p-4">
      <Card className="w-full max-w-md border-border/60 shadow-lg">
        <CardHeader className="space-y-2">
          <div className="flex items-center justify-between mb-4">
            <Link href="/">
              <a>
                <Button variant="ghost" size="sm" className="pl-0 hover:bg-transparent hover:text-primary">
                  <ArrowLeft className="h-4 w-4 mr-1" />
                  Back
                </Button>
              </a>
            </Link>
            <div className="flex-1" />
          </div>
          <div className="flex justify-center mb-4">
            <img src={arapointLogo} alt="Arapoint" className="h-30 w-30 object-contain" />
          </div>
          <div className="text-center">
            <CardTitle className="text-2xl font-heading font-bold">Create an Account</CardTitle>
            <CardDescription>
              {step === "details" ? "Get started with secure identity verification" : "Enter the code sent to your email"}
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          {step === "details" ? (
            <form onSubmit={handleSignup} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="firstName">First name</Label>
                  <Input id="firstName" placeholder="Ade" required className="h-11" />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="lastName">Last name</Label>
                  <Input id="lastName" placeholder="Ojo" required className="h-11" />
                </div>
              </div>
              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input id="email" type="email" placeholder="name@example.com" required className="h-11" />
              </div>
              <div className="space-y-2">
                <Label htmlFor="phone">Phone Number</Label>
                <Input id="phone" type="tel" placeholder="+234..." required className="h-11" />
              </div>
              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <div className="relative">
                  <Input 
                    id="password" 
                    type={showPassword ? "text" : "password"} 
                    required 
                    className="h-11 pr-10" 
                    data-testid="input-password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
                    data-testid="button-toggle-password"
                  >
                    {showPassword ? (
                      <EyeOff className="h-4 w-4" />
                    ) : (
                      <Eye className="h-4 w-4" />
                    )}
                  </button>
                </div>
              </div>
              <Button type="submit" className="w-full h-11 text-base" disabled={isLoading}>
                {isLoading ? "Sending Code..." : "Sign Up"}
              </Button>
            </form>
          ) : (
            <form onSubmit={handleVerify} className="space-y-6">
               <div className="space-y-2">
                <Label htmlFor="otp">One-Time Password (OTP)</Label>
                <Input id="otp" placeholder="123456" className="h-11 text-center text-lg tracking-widest" maxLength={6} required />
                <p className="text-xs text-muted-foreground text-center">Code sent to your email</p>
              </div>
              <Button type="submit" className="w-full h-11 text-base" disabled={isLoading}>
                {isLoading ? "Verifying..." : "Verify & Create Account"}
              </Button>
              <Button type="button" variant="ghost" className="w-full" onClick={() => setStep("details")}>
                Back to details
              </Button>
            </form>
          )}
        </CardContent>
        <CardFooter className="justify-center border-t pt-6">
          <p className="text-sm text-muted-foreground">
            Already have an account?{" "}
            <Link href="/auth/login">
              <a className="text-primary hover:underline font-medium">Sign in</a>
            </Link>
          </p>
        </CardFooter>
      </Card>
    </div>
  );
}

