client/src/pages/admin/AdminUserManageme.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { ArrowLeft, Edit2, Trash2, Eye } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { MOCK_ADMIN_USERS, AdminUser } from "@/lib/mockData";

const getStatusColor = (status: string) => {
  switch(status) {
    case 'active': return 'bg-green-100 text-green-700';
    case 'inactive': return 'bg-gray-100 text-gray-700';
    case 'suspended': return 'bg-red-100 text-red-700';
    default: return 'bg-gray-100 text-gray-700';
  }
};

export default function AdminUserManagement() {
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [users, setUsers] = useState(MOCK_ADMIN_USERS);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedUser, setSelectedUser] = useState<AdminUser | null>(null);
  const [editStatus, setEditStatus] = useState<"active" | "inactive" | "suspended">("active");

  const handleUpdateStatus = (userId: string, status: "active" | "inactive" | "suspended") => {
    setUsers(users.map(u => u.id === userId ? { ...u, status } : u));
    setSelectedUser(null);
    toast({ title: "User Status Updated", description: `User status changed to ${status}.` });
  };

  const handleDelete = (userId: string) => {
    setUsers(users.filter(u => u.id !== userId));
    toast({ title: "Deleted", description: "User account deleted." });
  };

  const filteredUsers = users.filter(u => 
    (statusFilter === "all" || u.status === statusFilter) &&
    (searchTerm === "" || u.name.toLowerCase().includes(searchTerm.toLowerCase()) || u.email.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-heading font-bold tracking-tight">User Management</h2>
          <p className="text-muted-foreground">Manage user accounts, status, and access</p>
        </div>
        <Button variant="outline" onClick={() => navigate("/admin")} data-testid="button-back">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back
        </Button>
      </div>

      <Card>
        <CardHeader>
          <div className="space-y-4">
            <div>
              <CardTitle>Search & Filter</CardTitle>
              <CardDescription>Find users by name or email</CardDescription>
            </div>
            <div className="flex gap-4">
              <Input 
                placeholder="Search by name or email..." 
                value={searchTerm} 
                onChange={(e) => setSearchTerm(e.target.value)}
                className="flex-1"
              />
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-40">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                  <SelectItem value="suspended">Suspended</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>All Users ({filteredUsers.length})</CardTitle>
          <CardDescription>Total registered users on the platform</CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Email</TableHead>
                <TableHead>Phone</TableHead>
                <TableHead>Wallet Balance</TableHead>
                <TableHead>Transactions</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Join Date</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredUsers.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={8} className="text-center py-8 text-muted-foreground">
                    No users found
                  </TableCell>
                </TableRow>
              ) : (
                filteredUsers.map((user) => (
                  <TableRow key={user.id}>
                    <TableCell className="font-medium">{user.name}</TableCell>
                    <TableCell className="text-sm">{user.email}</TableCell>
                    <TableCell className="text-sm">{user.phone}</TableCell>
                    <TableCell className="font-semibold">₦{user.walletBalance.toLocaleString()}</TableCell>
                    <TableCell className="text-center">{user.totalTransactions}</TableCell>
                    <TableCell>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(user.status)}`}>
                        {user.status}
                      </span>
                    </TableCell>
                    <TableCell className="text-sm">{new Date(user.joinDate).toLocaleDateString()}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button size="sm" variant="ghost" onClick={() => {
                              setSelectedUser(user);
                              setEditStatus(user.status);
                            }}>
                              <Eye className="h-4 w-4" />
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>User Details</DialogTitle>
                            </DialogHeader>
                            {selectedUser && (
                              <div className="space-y-4 py-4">
                                <div className="grid grid-cols-2 gap-4">
                                  <div>
                                    <p className="text-xs text-muted-foreground">User ID</p>
                                    <p className="font-mono font-bold mt-1">{selectedUser.id}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Name</p>
                                    <p className="font-bold mt-1">{selectedUser.name}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Email</p>
                                    <p className="font-bold mt-1">{selectedUser.email}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Phone</p>
                                    <p className="font-bold mt-1">{selectedUser.phone}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Wallet Balance</p>
                                    <p className="font-bold text-green-600 mt-1">₦{selectedUser.walletBalance.toLocaleString()}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Transactions</p>
                                    <p className="font-bold mt-1">{selectedUser.totalTransactions}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Join Date</p>
                                    <p className="font-bold mt-1">{new Date(selectedUser.joinDate).toLocaleDateString()}</p>
                                  </div>
                                  <div>
                                    <p className="text-xs text-muted-foreground">Status</p>
                                    <Select value={editStatus} onValueChange={(val: any) => setEditStatus(val)}>
                                      <SelectTrigger className="mt-1">
                                        <SelectValue />
                                      </SelectTrigger>
                                      <SelectContent>
                                        <SelectItem value="active">Active</SelectItem>
                                        <SelectItem value="inactive">Inactive</SelectItem>
                                        <SelectItem value="suspended">Suspended</SelectItem>
                                      </SelectContent>
                                    </Select>
                                  </div>
                                </div>
                              </div>
                            )}
                            <DialogFooter className="flex gap-2">
                              <Button 
                                variant="outline"
                                onClick={() => {
                                  if (selectedUser) {
                                    handleUpdateStatus(selectedUser.id, editStatus);
                                  }
                                }}
                              >
                                <Edit2 className="h-4 w-4 mr-2" />
                                Update Status
                              </Button>
                            </DialogFooter>
                          </DialogContent>
                        </Dialog>
                        <Button size="sm" variant="ghost" onClick={() => handleDelete(user.id)}>
                          <Trash2 className="h-4 w-4 text-red-600" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

