    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setRetrievedData({
        bvn: bvn,
        surname: "Ojo",
        firstname: "Adekunle",
        othername: "Femi",
        dateOfBirth: "15/03/1990",
        gender: "Male",
        phoneNumber: "08012345678",
        email: "adekunle.ojo@example.com",
        registrationDate: "2015-08-20",
        registrationStatus: "Active",
        tier: "Tier 1",
      });

      toast({
        title: "BVN Retrieved Successfully",
        description: `${selectedService === 'retrieval' ? 'Your BVN details have been retrieved.' : 'Your BVN information is ready.'}`,
      });
    }, 1500);
  };

  const handleModificationSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!bvn.trim() || bvn.length !== 11) {
      toast({
        title: "Invalid BVN",
        description: "BVN must be exactly 11 digits.",
        variant: "destructive",
      });
      return;
    }

    if (!changeCategory) {
      toast({
        title: "Category Required",
        description: "Please select a category of change.",
        variant: "destructive",
      });
      return;
    }

    if (changeCategory === "name" && (!oldName.trim() || !newName.trim())) {
      toast({
        title: "Name Fields Required",
        description: "Please enter both old and new names.",
        variant: "destructive",
      });
      return;
    }

    if (changeCategory === "dob" && (!oldDOB.trim() || !newDOB.trim())) {
      toast({
        title: "Date of Birth Fields Required",
        description: "Please enter both old and new dates of birth.",
        variant: "destructive",
      });
      return;
    }

    if (!phoneNumber.trim()) {
      toast({
        title: "Phone Number Required",
        description: "Please enter your phone number.",
        variant: "destructive",
      });
      return;
    }

    if (!address.trim()) {
      toast({
        title: "Address Required",
        description: "Please enter your address.",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setSubmitted(true);
      toast({
        title: "Modification Request Submitted",
        description: "Your BVN modification request has been submitted successfully.",
      });
    }, 1500);
  };

  const handleDownload = () => {
    const fileName = 
      selectedService === 'card' ? 'BVN_Card.pdf' :
      selectedService === 'modification' ? 'BVN_Modification_Receipt.pdf' :
      'BVN_Slip.pdf';

    toast({
      title: "Download Started",
      description: `Your ${fileName} is being downloaded.`,
    });
  };

  // Service selection view
  if (!selectedService) {
    return (
      <div className="space-y-6">
        <div>
          <h2 className="text-3xl font-heading font-bold tracking-tight">BVN Services</h2>
          <p className="text-muted-foreground">Select a BVN service to get started.</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {BVN_SERVICES.map((service) => (
            <Card 
              key={service.id}
              className="cursor-pointer hover:shadow-lg transition-all hover:-translate-y-1 group"
              onClick={() => setSelectedService(service.id)}
              data-testid={`card-service-${service.id}`}
            >
              <CardContent className="p-6 flex flex-col items-center text-center gap-4">
                <div className={`h-12 w-12 rounded-full flex items-center justify-center ${service.bg} ${service.color}`}>
                  <service.icon className="h-6 w-6" />
                </div>
                <div>
                  <h3 className="font-bold text-lg">{service.name}</h3>
                  <p className="text-sm text-muted-foreground mt-1">{service.desc}</p>
                </div>
                <Button className="w-full mt-2" size="sm">
                  Select
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  // BVN Modification Form View
  if (selectedService === 'modification' && !submitted) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-heading font-bold tracking-tight">BVN Modification</h2>
            <p className="text-muted-foreground">Update your BVN personal details</p>
          </div>
          <Button variant="outline" onClick={() => {
            setSelectedService(null);
            setBvn("");
            setChangeCategory("");
            setOldName("");
            setNewName("");
            setOldDOB("");
            setNewDOB("");
            setPhoneNumber("");
            setAddress("");
          }} data-testid="button-back-services">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back
          </Button>
        </div>

        <Card className="w-full max-w-2xl">
          <CardHeader>
            <CardTitle>Submit Modification Request</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleModificationSubmit} className="space-y-6">
              {/* BVN Input */}
              <div className="space-y-2">
                <Label htmlFor="bvn">Bank Verification Number</Label>
                <Input
                  id="bvn"
                  placeholder="11-digit BVN"
                  maxLength={11}
                  value={bvn}
                  onChange={(e) => setBvn(e.target.value.replace(/\D/g, ""))}
                  className="h-11 font-mono text-lg tracking-widest"
                  data-testid="input-bvn"
                />
                <p className="text-xs text-muted-foreground">Format: 11 digits only</p>
              </div>

              {/* Category Selection */}
              <div className="space-y-2">
                <Label htmlFor="category">Category of Change</Label>
                <Select value={changeCategory} onValueChange={(value: any) => setChangeCategory(value)}>
                  <SelectTrigger className="h-11">
                    <SelectValue placeholder="Select category of change" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="name">Change of Name</SelectItem>
                    <SelectItem value="dob">Change of Date of Birth</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Name Change Fields */}
              {changeCategory === "name" && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="oldName">Old Name</Label>
                    <Input
                      id="oldName"
                      placeholder="Your current name"
                      value={oldName}
                      onChange={(e) => setOldName(e.target.value)}
                      className="h-11"
                      data-testid="input-old-name"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="newName">New Name</Label>
                    <Input
                      id="newName"
                      placeholder="Your new name"
                      value={newName}
                      onChange={(e) => setNewName(e.target.value)}
                      className="h-11"
                      data-testid="input-new-name"
                    />
                  </div>
                </div>
              )}

              {/* Date of Birth Change Fields */}
              {changeCategory === "dob" && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="oldDOB">Old Date of Birth</Label>
                    <Input
                      id="oldDOB"
                      type="date"
                      value={oldDOB}
                      onChange={(e) => setOldDOB(e.target.value)}
                      className="h-11"
                      data-testid="input-old-dob"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="newDOB">New Date of Birth</Label>
                    <Input
                      id="newDOB"
                      type="date"
                      value={newDOB}
                      onChange={(e) => setNewDOB(e.target.value)}
                      className="h-11"
                      data-testid="input-new-dob"
                    />
                  </div>
                </div>
              )}

              {/* Phone Number */}
              <div className="space-y-2">
                <Label htmlFor="phone">Phone Number</Label>
                <Input
                  id="phone"
                  type="tel"
                  placeholder="+234..."
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  className="h-11"
                  data-testid="input-phone"
                />
              </div>

              {/* Address */}
              <div className="space-y-2">
                <Label htmlFor="address">Address</Label>
                <textarea
                  id="address"
                  placeholder="Enter your residential address"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 min-h-24 resize-none"
                  data-testid="textarea-address"
                />
              </div>

              {/* Submit Button */}
              <Button type="submit" className="w-full h-11 text-base" disabled={loading} data-testid="button-submit-modification">
                {loading ? (
                  <>
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                    Submitting...
                  </>
                ) : "Submit Modification Request"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Modification Success View
  if (selectedService === 'modification' && submitted) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-heading font-bold tracking-tight">Modification Submitted</h2>
            <p className="text-muted-foreground">Your request has been successfully submitted</p>
          </div>
          <Button variant="outline" onClick={() => {
            setSelectedService(null);
            setBvn("");
            setChangeCategory("");
            setOldName("");
            setNewName("");
            setOldDOB("");
            setNewDOB("");
            setPhoneNumber("");
            setAddress("");
            setSubmitted(false);
          }} data-testid="button-back-services">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back
          </Button>
        </div>

        <Card className="w-full max-w-2xl bg-gradient-to-br from-green-50 to-emerald-50 border-2 border-green-200">
          <CardContent className="pt-8">
            <div className="flex flex-col items-center gap-6 text-center">
              <div className="h-16 w-16 rounded-full bg-green-100 flex items-center justify-center">
                <CheckCircle2 className="h-8 w-8 text-green-600" />
              </div>

              <div className="space-y-2">
                <h3 className="text-2xl font-bold text-green-900">Request Submitted Successfully</h3>
                <p className="text-green-700">Your BVN modification request has been received and is under review.</p>
              </div>

              <div className="bg-white rounded-lg p-6 w-full space-y-4 border border-green-200">
                <div className="text-left space-y-3">
                  <div>
                    <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">BVN Number</p>
                    <p className="text-lg font-mono font-bold text-foreground mt-1">{bvn}</p>
                  </div>
                  <div>
                    <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Category of Change</p>
                    <p className="text-lg font-medium text-foreground mt-1">{changeCategory === 'name' ? 'Change of Name' : 'Change of Date of Birth'}</p>
                  </div>
                  <div>
                    <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Phone Number</p>
                    <p className="text-lg font-medium text-foreground mt-1">{phoneNumber}</p>
                  </div>
                </div>
              </div>

              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 w-full text-sm text-blue-900">
                <p><strong>Next Steps:</strong> Your request will be processed within 5-7 business days. You will receive a confirmation via email and SMS.</p>
              </div>

              <Button 
                onClick={handleDownload}
                className="w-full h-11"
                data-testid="button-download-receipt"
              >
                <Download className="h-4 w-4 mr-2" />
                Download Receipt
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Query input view (for retrieval and card)
  if (!retrievedData) {
    const serviceName = BVN_SERVICES.find(s => s.id === selectedService)?.name;

    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-heading font-bold tracking-tight">{serviceName}</h2>
            <p className="text-muted-foreground">Enter your 11-digit BVN to proceed.</p>
          </div>
          <Button variant="outline" onClick={() => {
            setSelectedService(null);
            setBvn("");
          }} data-testid="button-back-services">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back
          </Button>
        </div>

        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>Enter BVN</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleQuery} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="bvn">Bank Verification Number</Label>
                <Input
                  id="bvn"
                  placeholder="11-digit BVN"
                  maxLength={11}
                  value={bvn}
                  onChange={(e) => setBvn(e.target.value.replace(/\D/g, ""))}
                  className="h-11 font-mono text-lg tracking-widest"
                  data-testid="input-bvn"
                />
                <p className="text-xs text-muted-foreground">Format: 11 digits only</p>
              </div>

              <Button type="submit" className="w-full h-11" disabled={loading} data-testid="button-query-bvn">
                {loading ? <Loader2 className="h-4 w-4 animate-spin mr-2" /> : "Continue"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Results view (retrieval and card)
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-heading font-bold tracking-tight">
            {selectedService === 'card' ? 'BVN Digital Card' : 'BVN Slip'}
          </h2>
          <p className="text-muted-foreground">Your verified BVN information</p>
        </div>
        <Button variant="outline" onClick={() => {
          setRetrievedData(null);
          setBvn("");
          setSelectedService(null);
        }} data-testid="button-back-services">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back
        </Button>
      </div>

      {/* BVN Slip */}
      <Card className="w-full max-w-2xl bg-gradient-to-br from-slate-50 to-slate-100 border-2 border-primary/30">
        <CardHeader className="bg-gradient-to-r from-primary to-primary/80 text-primary-foreground rounded-t-lg">
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="text-2xl">
                {selectedService === 'card' ? 'Bank Verification Number Card' : 'Bank Verification Number Slip'}
              </CardTitle>
              <p className="text-primary-foreground/80 text-sm mt-1">Official Verification Document</p>
            </div>
            <Button 
              onClick={handleDownload}
              className="bg-primary-foreground text-primary hover:bg-primary-foreground/90"
              data-testid="button-download-slip"
            >
              <Download className="h-4 w-4 mr-2" />
              Download
            </Button>
          </div>
        </CardHeader>

        <CardContent className="pt-8 pb-8">
          <div className="space-y-8">
            {/* BVN Section */}
            <div className="border-b-2 border-dashed border-primary/20 pb-6">
              <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest mb-2">BVN Number</p>
              <p className="text-4xl font-mono font-bold text-primary tracking-[0.15em]">{retrievedData.bvn}</p>
            </div>

            {/* Personal Information */}
            <div className="space-y-4">
              <h3 className="font-semibold text-lg border-b pb-2">Personal Information</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Surname</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.surname}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">First Name</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.firstname}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Other Name</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.othername}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Date of Birth</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.dateOfBirth}</p>
                </div>
              </div>
            </div>

            {/* Contact & Registration Details */}
            <div className="space-y-4">
              <h3 className="font-semibold text-lg border-b pb-2">Contact & Registration Details</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Phone Number</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.phoneNumber}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Email Address</p>
                  <p className="text-lg font-medium mt-1 break-all">{retrievedData.email}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Gender</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.gender}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Registration Date</p>
                  <p className="text-lg font-medium mt-1">{retrievedData.registrationDate}</p>
                </div>
              </div>
            </div>

            {/* Status Information */}
            <div className="bg-primary/5 border border-primary/20 rounded-lg p-4 space-y-3">
              <div className="flex justify-between">
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Registration Status</p>
                  <p className="text-lg font-semibold text-green-600 mt-1">{retrievedData.registrationStatus}</p>
                </div>
                <div>
                  <p className="text-xs font-semibold text-muted-foreground uppercase tracking-widest">Tier</p>
                  <p className="text-lg font-semibold mt-1">{retrievedData.tier}</p>
                </div>
              </div>
            </div>

            {/* Footer */}
            <div className="border-t-2 border-dashed border-primary/20 pt-6 text-center text-xs text-muted-foreground">
              <p>This is an official BVN verification document from Arapoint</p>
              <p className="mt-1">Generated on {new Date().toLocaleDateString()} at {new Date().toLocaleTimeString()}</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

