                <div className="border-l-4 border-primary pl-4">
                  <p className="text-sm text-muted-foreground">Provider</p>
                  <p className="font-semibold capitalize">{formData.provider}</p>
                </div>
                <div className="border-l-4 border-primary pl-4">
                  <p className="text-sm text-muted-foreground">IUC/Smartcard Number</p>
                  <p className="font-semibold">{formData.iucNumber}</p>
                </div>
                <div className="border-l-4 border-primary pl-4">
                  <p className="text-sm text-muted-foreground">Package</p>
                  <p className="font-semibold">{formData.package}</p>
                </div>
              </>
            )}
          </div>

          <DialogFooter className="gap-2 sm:gap-0">
            <Button 
              variant="outline" 
              onClick={() => setShowConfirmation(false)}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button 
              onClick={handleConfirm}
              disabled={loading}
              data-testid="button-confirm-transaction"
            >
              {loading ? <Loader2 className="h-4 w-4 animate-spin mr-2" /> : ""}
              Confirm Transaction
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

