client/src/pages/auth/Login.tsx.content:
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowLeft, Eye, EyeOff } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import arapointLogo from "@assets/generated_images/arapoint_logo_-_security_shield_with_checkmark.png";

export default function Login() {
  const [_, setLocation] = useLocation();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    // Simulate API call
    setTimeout(() => {
      setIsLoading(false);
      toast({
        title: "Welcome back!",
        description: "Successfully logged in.",
      });
      setLocation("/dashboard");
    }, 1500);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-muted/30 p-4">
      <Card className="w-full max-w-md border-border/60 shadow-lg">
        <CardHeader className="space-y-2">
          <div className="flex items-center justify-between mb-4">
            <Link href="/">
              <a>
                <Button variant="ghost" size="sm" className="pl-0 hover:bg-transparent hover:text-primary">
                  <ArrowLeft className="h-4 w-4 mr-1" />
                  Back
                </Button>
              </a>
            </Link>
            <div className="flex-1" />
          </div>
          <div className="flex justify-center mb-4">
            <img src={arapointLogo} alt="Arapoint" className="h-30 w-30 object-contain" />
          </div>
          <div className="text-center">
            <CardTitle className="text-2xl font-heading font-bold">Sign in to Arapoint</CardTitle>
            <CardDescription className="mt-2">
              Enter your credentials to access your dashboard
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleLogin} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input id="email" type="email" placeholder="name@example.com" required className="h-11" />
            </div>
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <Label htmlFor="password">Password</Label>
                <Link href="/auth/reset">
                  <a className="text-xs text-primary hover:underline">Forgot password?</a>
                </Link>
              </div>
              <div className="relative">
                <Input 
                  id="password" 
                  type={showPassword ? "text" : "password"} 
                  required 
                  className="h-11 pr-10" 
                  data-testid="input-password"
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
                  data-testid="button-toggle-password"
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </button>
              </div>
            </div>
            <Button type="submit" className="w-full h-11 text-base" disabled={isLoading}>
              {isLoading ? "Signing in..." : "Sign In"}
            </Button>
          </form>
        </CardContent>
        <CardFooter className="justify-center border-t pt-6">
          <p className="text-sm text-muted-foreground">
            Don't have an account?{" "}
            <Link href="/auth/signup">
              <a className="text-primary hover:underline font-medium">Sign up</a>
            </Link>
          </p>
        </CardFooter>
      </Card>
    </div>
  );
}

client/src/pages/dashboard/FundWallet.ts.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { ArrowLeft, CreditCard, Copy, CheckCircle2 } from "lucide-react";
import { Link } from "wouter";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

const PAYMENT_GATEWAYS = [
  {
    id: 1,
    bankName: "First Bank",
    icon: "🏦",
  },
  {
    id: 2,
    bankName: "Zenith Bank",
    icon: "🏢",
  },
  {
    id: 3,
    bankName: "GTBank",
    icon: "🏛️",
  },
];

const GENERATED_ACCOUNTS = {
  1: { accountNumber: "1234567890", accountName: "Arapoint Services", bankName: "First Bank" },
  2: { accountNumber: "0987654321", accountName: "Arapoint Limited", bankName: "Zenith Bank" },
  3: { accountNumber: "5555666677", accountName: "Arapoint Payment", bankName: "GTBank" },
};

export default function FundWallet() {
  const [selectedAccount, setSelectedAccount] = useState<number | null>(null);
  const [copied, setCopied] = useState(false);

  const account = selectedAccount ? GENERATED_ACCOUNTS[selectedAccount as keyof typeof GENERATED_ACCOUNTS] : null;

  const handleCopy = () => {
    if (account) {
      navigator.clipboard.writeText(account.accountNumber);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="space-y-6 max-w-5xl mx-auto">
      <div className="flex items-center gap-4 mb-8">
        <Link href="/dashboard">
          <Button variant="ghost" size="icon" className="rounded-full">
            <ArrowLeft className="h-5 w-5" />
          </Button>
        </Link>
        <div>
          <h2 className="text-2xl font-heading font-bold">Fund Your Wallet</h2>
          <p className="text-muted-foreground">Select a payment method</p>
        </div>
      </div>

      {/* Payment Gateway Cards */}
      <div className="grid md:grid-cols-3 gap-6">
        {PAYMENT_GATEWAYS.map((gateway) => (
          <Card 
            key={gateway.id} 
            className="border-2 hover:border-primary transition-all cursor-pointer hover:shadow-lg hover:-translate-y-1"
            onClick={() => setSelectedAccount(gateway.id)}
          >
            <CardContent className="p-8 flex flex-col items-center gap-4 h-full justify-center">
              <div className="text-5xl">{gateway.icon}</div>
              <h3 className="text-lg font-bold text-center">{gateway.bankName}</h3>
              <Button 
                className="w-full mt-4"
                onClick={(e) => {
                  e.stopPropagation();
                  setSelectedAccount(gateway.id);
                }}
              >
                <CreditCard className="h-4 w-4 mr-2" />
                Payment Information
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Payment Details Modal */}
      <Dialog open={selectedAccount !== null} onOpenChange={(open) => {
        if (!open) setSelectedAccount(null);
      }}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Payment Information</DialogTitle>
            <DialogDescription>Account details generated by payment gateway</DialogDescription>
          </DialogHeader>

          {account && (
            <div className="space-y-6">
              <div className="bg-primary/10 rounded-lg p-6 space-y-4">
                <div>
                  <p className="text-xs text-muted-foreground uppercase font-semibold mb-1">Account Number</p>
                  <div className="flex items-center gap-2">
                    <p className="font-mono font-bold text-lg tracking-widest">{account.accountNumber}</p>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={handleCopy}
                      className="h-8 w-8 p-0"
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                  </div>
                  {copied && (
                    <p className="text-xs text-green-600 mt-1 flex items-center gap-1">
                      <CheckCircle2 className="h-3 w-3" /> Copied to clipboard
                    </p>
                  )}
                </div>

                <div className="border-t pt-4">
                  <p className="text-xs text-muted-foreground uppercase font-semibold mb-1">Account Name</p>
                  <p className="font-medium text-sm">{account.accountName}</p>
                </div>

                <div className="border-t pt-4">
                  <p className="text-xs text-muted-foreground uppercase font-semibold mb-1">Bank Name</p>
                  <p className="font-medium text-sm">{account.bankName}</p>
                </div>
              </div>

              <Button className="w-full" size="lg">
                <CreditCard className="h-4 w-4 mr-2" />
                Continue to Payment Gateway
              </Button>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/ui/button-group.ts.content:
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import { Separator } from "@/components/ui/separator"

const buttonGroupVariants = cva(
  "flex w-fit items-stretch has-[>[data-slot=button-group]]:gap-2 [&>*]:focus-visible:relative [&>*]:focus-visible:z-10 has-[select[aria-hidden=true]:last-child]:[&>[data-slot=select-trigger]:last-of-type]:rounded-r-md [&>[data-slot=select-trigger]:not([class*='w-'])]:w-fit [&>input]:flex-1",
  {
    variants: {
      orientation: {
        horizontal:
          "[&>*:not(:first-child)]:rounded-l-none [&>*:not(:first-child)]:border-l-0 [&>*:not(:last-child)]:rounded-r-none",
        vertical:
          "flex-col [&>*:not(:first-child)]:rounded-t-none [&>*:not(:first-child)]:border-t-0 [&>*:not(:last-child)]:rounded-b-none",
      },
    },
    defaultVariants: {
      orientation: "horizontal",
    },
  }
)

function ButtonGroup({
  className,
  orientation,
  ...props
}: React.ComponentProps<"div"> & VariantProps<typeof buttonGroupVariants>) {
  return (
    <div
      role="group"
      data-slot="button-group"
      data-orientation={orientation}
      className={cn(buttonGroupVariants({ orientation }), className)}
      {...props}
    />
  )
}

function ButtonGroupText({
  className,
  asChild = false,
  ...props
}: React.ComponentProps<"div"> & {
  asChild?: boolean
}) {
  const Comp = asChild ? Slot : "div"

  return (
    <Comp
      className={cn(
        "bg-muted shadow-xs flex items-center gap-2 rounded-md border px-4 text-sm font-medium [&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none",
        className
      )}
      {...props}
    />
  )
}

function ButtonGroupSeparator({
  className,
  orientation = "vertical",
  ...props
}: React.ComponentProps<typeof Separator>) {
  return (
    <Separator
      data-slot="button-group-separator"
      orientation={orientation}
      className={cn(
        "bg-input relative !m-0 self-stretch data-[orientation=vertical]:h-auto",
        className
      )}
      {...props}
    />
  )
}

export {
  ButtonGroup,
  ButtonGroupSeparator,
  ButtonGroupText,
  buttonGroupVariants,
}

