const inputGroupAddonVariants = cva(
  "text-muted-foreground flex h-auto cursor-text select-none items-center justify-center gap-2 py-1.5 text-sm font-medium group-data-[disabled=true]/input-group:opacity-50 [&>kbd]:rounded-[calc(var(--radius)-5px)] [&>svg:not([class*='size-'])]:size-4",
  {
    variants: {
      align: {
        "inline-start":
          "order-first pl-3 has-[>button]:ml-[-0.45rem] has-[>kbd]:ml-[-0.35rem]",
        "inline-end":
          "order-last pr-3 has-[>button]:mr-[-0.4rem] has-[>kbd]:mr-[-0.35rem]",
        "block-start":
          "[.border-b]:pb-3 order-first w-full justify-start px-3 pt-3 group-has-[>input]/input-group:pt-2.5",
        "block-end":
          "[.border-t]:pt-3 order-last w-full justify-start px-3 pb-3 group-has-[>input]/input-group:pb-2.5",
      },
    },
    defaultVariants: {
      align: "inline-start",
    },
  }
)

function InputGroupAddon({
  className,
  align = "inline-start",
  ...props
}: React.ComponentProps<"div"> & VariantProps<typeof inputGroupAddonVariants>) {
  return (
    <div
      role="group"
      data-slot="input-group-addon"
      data-align={align}
      className={cn(inputGroupAddonVariants({ align }), className)}
      onClick={(e) => {
        if ((e.target as HTMLElement).closest("button")) {
          return
        }
        e.currentTarget.parentElement?.querySelector("input")?.focus()
      }}
      {...props}
    />
  )
}

const inputGroupButtonVariants = cva(
  "flex items-center gap-2 text-sm shadow-none",
  {
    variants: {
      size: {
        xs: "h-6 gap-1 rounded-[calc(var(--radius)-5px)] px-2 has-[>svg]:px-2 [&>svg:not([class*='size-'])]:size-3.5",
        sm: "h-8 gap-1.5 rounded-md px-2.5 has-[>svg]:px-2.5",
        "icon-xs":
          "size-6 rounded-[calc(var(--radius)-5px)] p-0 has-[>svg]:p-0",
        "icon-sm": "size-8 p-0 has-[>svg]:p-0",
      },
    },
    defaultVariants: {
      size: "xs",
    },
  }
)

function InputGroupButton({
  className,
  type = "button",
  variant = "ghost",
  size = "xs",
  ...props
}: Omit<React.ComponentProps<typeof Button>, "size"> &
  VariantProps<typeof inputGroupButtonVariants>) {
  return (
    <Button
      type={type}
      data-size={size}
      variant={variant}
      className={cn(inputGroupButtonVariants({ size }), className)}
      {...props}
    />
  )
}

function InputGroupText({ className, ...props }: React.ComponentProps<"span">) {
  return (
    <span
      className={cn(
        "text-muted-foreground flex items-center gap-2 text-sm [&_svg:not([class*='size-'])]:size-4 [&_svg]:pointer-events-none",
        className
      )}
      {...props}
    />
  )
}

function InputGroupInput({
  className,
  ...props
}: React.ComponentProps<"input">) {
  return (
    <Input
      data-slot="input-group-control"
      className={cn(
        "flex-1 rounded-none border-0 bg-transparent shadow-none focus-visible:ring-0 dark:bg-transparent",
        className
      )}
      {...props}
    />
  )
}

function InputGroupTextarea({
  className,
  ...props
}: React.ComponentProps<"textarea">) {
  return (
    <Textarea
      data-slot="input-group-control"
      className={cn(
        "flex-1 resize-none rounded-none border-0 bg-transparent py-3 shadow-none focus-visible:ring-0 dark:bg-transparent",
        className
      )}
      {...props}
    />
  )
}

export {
  InputGroup,
  InputGroupAddon,
  InputGroupButton,
  InputGroupText,
  InputGroupInput,
  InputGroupTextarea,
}

client/src/pages/dashboard/identity/Iden.content:
import { useRoute, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle, CardFooter } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { ArrowLeft, Loader2, Search, AlertCircle, CheckCircle2, Download, Printer, Clock, FileText } from "lucide-react";
import { SERVICES } from "../IdentityVerification";
import { useState } from "react";
import { MOCK_USER, MOCK_PRICES } from "@/lib/mockData";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import slipInfo from '@assets/image_1764211401623.png';
import slipRegular from '@assets/image_1764211451522.png';
import slipStandard from '@assets/image_1764211490940.png';
import slipPremium from '@assets/image_1764211520708.png';

const SLIP_TYPES = [
  { id: "information", name: "Information Slip", price: 200, desc: "Verified NIN Details", image: slipInfo },
  { id: "regular", name: "Regular Slip", price: 250, desc: "National Identification Number Slip (NINS)", image: slipRegular },
  { id: "standard", name: "Standard Slip", price: 300, desc: "National Identification Card (NGA)", image: slipStandard },
  { id: "premium", name: "Premium Slip", price: 300, desc: "Digital NIN Slip", image: slipPremium },
];

export default function IdentityServiceRouter() {
  const [match, params] = useRoute("/dashboard/identity/:service");
  const serviceId = params?.service;
  const service = SERVICES.find(s => s.id === serviceId);
  const priceInfo = MOCK_PRICES.find(p => p.id === serviceId);
  const price = priceInfo ? priceInfo.price : 500;

  if (!service) {
    return <div>Service not found</div>;
  }

  return (
    <div className="space-y-6 max-w-4xl mx-auto">
      <div className="flex items-center gap-4 mb-8">
        <Link href="/dashboard/identity">
          <Button variant="ghost" size="icon" className="rounded-full">
            <ArrowLeft className="h-5 w-5" />
          </Button>
        </Link>
        <div>
          <h2 className="text-2xl font-heading font-bold flex items-center gap-2">
            <service.icon className={`h-6 w-6 ${service.color}`} />
            {service.name}
          </h2>
          <p className="text-muted-foreground">{service.desc}</p>
        </div>
      </div>

      <ServiceContent service={service} price={price} />
    </div>
  );
}

function ServiceContent({ service, price }: { service: any, price: number }) {
  const [isLoading, setIsLoading] = useState(false);
  const [requestStatus, setRequestStatus] = useState<"idle" | "pending" | "completed">("idle");
  const [result, setResult] = useState<any>(null);
  const [selectedSlip, setSelectedSlip] = useState("standard");
  const [bvnError, setBvnError] = useState("");

  const handleSearch = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const inputValue = formData.get("input") as string;

    // Validate BVN starts with 22 (only for BVN services, not IPE clearance or validation)
    if (!service.id.includes("nin") && service.id !== "nin-phone" && service.id !== "ipe-clearance" && service.id !== "validation") {
      if (!inputValue.startsWith("22")) {
        setBvnError("BVN must start with 22");
        return;
      }
    }

    setBvnError("");
    setIsLoading(true);

    if (service.id === "ipe-clearance" || service.id === "personalization" || service.id === "validation") {
      setTimeout(() => {
        setIsLoading(false);
        setRequestStatus("pending");
      }, 1500);
    } else {
      setTimeout(() => {
        setIsLoading(false);
        setRequestStatus("completed");
        setResult(getMockResult(service.id));
      }, 2000);
    }
  };

  if (requestStatus === "pending") {
    return (
      <Card className="max-w-lg mx-auto text-center border-yellow-200 bg-yellow-50 dark:bg-yellow-900/20 dark:border-yellow-800">
        <CardContent className="pt-10 pb-10 space-y-4">
          <div className="h-16 w-16 bg-yellow-100 rounded-full flex items-center justify-center mx-auto text-yellow-600">
            <Clock className="h-8 w-8" />
          </div>
          <h3 className="text-2xl font-bold text-yellow-800 dark:text-yellow-400">Request Submitted</h3>
          <p className="text-yellow-700 dark:text-yellow-300 max-w-xs mx-auto">
            Your {service.name} request has been submitted successfully. 
            Status is currently <strong>Pending Admin Approval</strong>.
          </p>
          <p className="text-sm text-muted-foreground">
            You will be notified once the admin processes your request and uploads the result slip.
          </p>
          <Button onClick={() => setRequestStatus("idle")} variant="outline" className="mt-4">Submit Another Request</Button>
        </CardContent>
      </Card>
    );
  }

  if (service.id.includes("verification") || service.id.includes("lookup") || service.id === "nin-phone") {
    return (
      <div className="grid lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Search Identity</CardTitle>
              <CardDescription>Enter the required details to verify.</CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSearch} className="space-y-6">
                {service.id === "nin-phone" ? (
                  <div className="space-y-2">
                    <Label>Phone Number</Label>
                    <Input 
                      name="input"
                      placeholder="08012345678" 
                      maxLength={11} 
                      inputMode="numeric"
                      pattern="[0-9]*"
                      required 
                      className="h-12 font-mono text-lg tracking-widest" 
                    />
                  </div>
                ) : service.id.includes("nin") || service.id === "validation" ? (
                  <div className="space-y-2">
                    <Label>National Identity Number (NIN)</Label>
                    <Input 
                      name="input"
                      placeholder="11 Digit NIN" 
                      maxLength={11} 
                      inputMode="numeric"
                      pattern="[0-9]*"
                      required 
                      className="h-12 font-mono text-lg tracking-widest" 
                    />
                  </div>
                ) : (
                  <div className="space-y-2">
                    <Label>Bank Verification Number (BVN)</Label>
                    <Input 
                      name="input"
                      placeholder="22XXXXXXXXXX" 
                      maxLength={11} 
                      inputMode="numeric"
                      pattern="[0-9]*"
                      required 
                      className={`h-12 font-mono text-lg tracking-widest ${bvnError ? 'border-red-500' : ''}`}
                    />
                    {bvnError && <p className="text-red-500 text-sm font-medium">{bvnError}</p>}
                  </div>
                )}

                <Alert className="bg-blue-50 text-blue-800 border-blue-200">
                  <AlertCircle className="h-4 w-4" />
                  <AlertTitle>Service Fee: ₦{price.toLocaleString()}</AlertTitle>
                  <AlertDescription>This amount will be deducted from your wallet.</AlertDescription>
                </Alert>

                <Button type="submit" size="lg" className="w-full" disabled={isLoading}>
                  {isLoading ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Processing Request...
                    </>
                  ) : (
                    <>
                      <Search className="mr-2 h-4 w-4" />
                      Verify Now
                    </>
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>

          {result && (
            <div className="space-y-6">
              <ResultCard result={result} type={service.id} />

              <Card>
                <CardHeader>
                  <CardTitle>Download Result Slip</CardTitle>
                  <CardDescription>Select your preferred slip format.</CardDescription>
                </CardHeader>
                <CardContent>
                  <RadioGroup value={selectedSlip} onValueChange={setSelectedSlip} className="grid grid-cols-2 gap-4">
                    {SLIP_TYPES.map((slip) => (
                      <div key={slip.id}>
                        <RadioGroupItem value={slip.id} id={slip.id} className="peer sr-only" />
                        <Label
                          htmlFor={slip.id}
                          className="flex flex-col items-center justify-between rounded-md border-2 border-muted bg-popover p-2 hover:bg-accent hover:text-accent-foreground peer-data-[state=checked]:border-primary [&:has([data-state=checked])]:border-primary cursor-pointer h-full text-center"
                        >
                          <div className="w-full aspect-[1.6] bg-muted mb-2 rounded overflow-hidden relative border">
                             <img src={slip.image} alt={slip.name} className="object-cover w-full h-full" />
                          </div>
                          <div className="space-y-1">
                            <p className="font-medium leading-none">{slip.name}</p>
                            <p className="text-xs text-muted-foreground">{slip.desc}</p>
                          </div>
                          <div className="mt-2 font-bold text-sm text-primary">
                            ₦{slip.price}
                          </div>
                        </Label>
                      </div>
                    ))}
                  </RadioGroup>
                  <div className="mt-6 flex justify-end">
                    <Button>
                      <Download className="mr-2 h-4 w-4" />
                      Download {SLIP_TYPES.find(s => s.id === selectedSlip)?.name}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </div>
          )}
        </div>

        <div className="space-y-6">
          <Card className="bg-muted/30 border-none">
            <CardHeader>
              <CardTitle className="text-base">Instructions</CardTitle>
            </CardHeader>
            <CardContent className="text-sm space-y-2 text-muted-foreground">
              <p>1. Ensure you have the correct number.</p>
              <p>2. Consent must be obtained from the ID holder.</p>
              <p>3. Results are fetched directly from NIMC/NIBSS.</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (service.id.includes("modification")) {
    return (
      <Card className="max-w-2xl">
        <CardHeader>
          <CardTitle>Correction Request Form</CardTitle>
          <CardDescription>Submit a request to update identity details.</CardDescription>
        </CardHeader>
        <CardContent>
          <form className="space-y-4">
             <div className="space-y-2">
              <Label>ID Number (NIN/BVN)</Label>
              <Input placeholder="Reference ID" />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label>Field to Modify</Label>
                <Input placeholder="e.g. Date of Birth" />
              </div>
              <div className="space-y-2">
                <Label>New Value</Label>
                <Input placeholder="Correct Value" />
              </div>
            </div>
            <div className="space-y-2">
              <Label>Supporting Document</Label>
              <Input type="file" />
              <p className="text-xs text-muted-foreground">Upload Affidavit or Newspaper Publication</p>
            </div>
            <Button className="w-full mt-4">Submit Request</Button>
          </form>
        </CardContent>
      </Card>
    );
  }

  if (service.id === "personalization" || service.id === "validation") {
    return (
      <Card className="max-w-2xl">
        <CardHeader>
          <CardTitle>{service.id === "personalization" ? "Personalization Request" : "Validation Request"}</CardTitle>
          <CardDescription>Submit a request to {service.id === "personalization" ? "personalize your identity details" : "validate your identity"}.</CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSearch} className="space-y-4">
             <div className="space-y-2">
              <Label>{service.id === "personalization" ? "Tracking ID / Reference" : "National Identity Number (NIN)"}</Label>
              <Input 
                name="input" 
                placeholder={service.id === "personalization" ? "Enter Tracking ID" : "11 Digit NIN"}
                className="h-12" 
                maxLength={service.id === "personalization" ? undefined : 11}
                inputMode={service.id === "personalization" ? "text" : "numeric"}
                pattern={service.id === "personalization" ? undefined : "[0-9]*"}
                required 
              />
            </div>
             <Alert className="bg-blue-50 text-blue-800 border-blue-200">
                <AlertCircle className="h-4 w-4" />
                <AlertTitle>Service Fee: ₦{price.toLocaleString()}</AlertTitle>
                <AlertDescription>This request will be processed by Admin. Status will update after approval.</AlertDescription>
              </Alert>
            <Button type="submit" className="w-full" size="lg" disabled={isLoading}>
               {isLoading ? <Loader2 className="animate-spin" /> : "Submit Request"}
            </Button>
          </form>
        </CardContent>
      </Card>
    );
  }

  if (service.id === "ipe-clearance") {
     return (
      <Card className="max-w-2xl">
        <CardHeader>
          <CardTitle>IPE Clearance Check</CardTitle>
          <CardDescription>Verify immigration clearance status.</CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSearch} className="space-y-4">
             <div className="space-y-2">
              <Label>Tracking ID / Reference</Label>
              <Input name="input" placeholder="Enter Tracking ID" className="h-12" required />
            </div>
             <Alert className="bg-blue-50 text-blue-800 border-blue-200">
                <AlertCircle className="h-4 w-4" />
                <AlertTitle>Service Fee: ₦{price.toLocaleString()}</AlertTitle>
                <AlertDescription>This request will be processed by Admin.</AlertDescription>
              </Alert>
            <Button type="submit" className="w-full" size="lg" disabled={isLoading}>
               {isLoading ? <Loader2 className="animate-spin" /> : "Submit for Clearance"}
            </Button>
          </form>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center py-12 text-center border rounded-lg bg-muted/10 border-dashed">
      <div className={`h-16 w-16 rounded-full flex items-center justify-center mb-4 ${service.bg} ${service.color}`}>
        <service.icon className="h-8 w-8" />
      </div>
      <h3 className="text-xl font-bold mb-2">{service.name}</h3>
      <p className="text-muted-foreground max-w-md mb-6">
        This service is currently being maintained or requires manual processing. Please contact support for assistance.
      </p>
      <Button variant="outline">Contact Support</Button>
    </div>
  );
}

function ResultCard({ result, type }: { result: any, type: string }) {
  return (
    <div className="animate-in fade-in slide-in-from-bottom-4 duration-500 mt-6">
      <Card className="border-primary/50 shadow-md overflow-hidden">
        <div className="bg-primary/10 p-4 border-b border-primary/20 flex items-center justify-between">
          <div className="flex items-center gap-2 text-primary font-bold">
            <CheckCircle2 className="h-5 w-5" />
            Identity Verified
          </div>
          <span className="text-xs text-muted-foreground font-mono">{new Date().toLocaleString()}</span>
        </div>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-6">
            <div className="flex-shrink-0">
              <img src={result.photo} alt="Face" className="w-32 h-32 rounded-lg object-cover border border-border shadow-sm bg-muted" />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-x-8 gap-y-4 flex-1">
              <div>
                <p className="text-xs text-muted-foreground uppercase">Full Name</p>
                <p className="font-medium text-lg">{result.lastName} {result.firstName} {result.middleName}</p>
              </div>
              <div>
                <p className="text-xs text-muted-foreground uppercase">Date of Birth</p>
                <p className="font-medium">{result.dob}</p>
              </div>
              <div>
                <p className="text-xs text-muted-foreground uppercase">Gender</p>
                <p className="font-medium">{result.gender}</p>
              </div>
              <div>
                 <p className="text-xs text-muted-foreground uppercase">Reference ID</p>
                <p className="font-medium font-mono">{result.nin}</p>
              </div>
               <div className="md:col-span-2">
                <p className="text-xs text-muted-foreground uppercase">Address</p>
                <p className="font-medium">{result.address}</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

function getMockResult(type: string) {
  return {
    firstName: "ADEOLA",
    lastName: "OGUNLEYE",
    middleName: "JOHN",
    dob: "15-05-1990",
    gender: "MALE",
    phone: "08012345678",
    nin: "12345678901",
    photo: MOCK_USER.avatar,
    address: "123, VICTORIA ISLAND, LAGOS",
    status: "VALID"
  };
}

client/src/components/ui/chart.tsx.content:
import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload
            .filter((item) => item.type !== "none")
            .map((item, index) => {
              const key = `${nameKey || item.name || item.dataKey || "value"}`
              const itemConfig = getPayloadConfigFromPayload(config, item, key)
              const indicatorColor = color || item.payload.fill || item.color

              return (
                <div
                  key={item.dataKey}
                  className={cn(
                    "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                    indicator === "dot" && "items-center"
                  )}
                >
                  {formatter && item?.value !== undefined && item.name ? (
                    formatter(item.value, item.name, item, index, item.payload)
                  ) : (
                    <>
                      {itemConfig?.icon ? (
                        <itemConfig.icon />
                      ) : (
                        !hideIndicator && (
                          <div
                            className={cn(
                              "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                              {
                                "h-2.5 w-2.5": indicator === "dot",
                                "w-1": indicator === "line",
                                "w-0 border-[1.5px] border-dashed bg-transparent":
                                  indicator === "dashed",
                                "my-0.5": nestLabel && indicator === "dashed",
                              }
                            )}
                            style={
                              {
                                "--color-bg": indicatorColor,
                                "--color-border": indicatorColor,
                              } as React.CSSProperties
                            }
                          />
                        )
                      )}
                      <div
                        className={cn(
                          "flex flex-1 justify-between leading-none",
                          nestLabel ? "items-end" : "items-center"
                        )}
                      >
                        <div className="grid gap-1.5">
                          {nestLabel ? tooltipLabel : null}
                          <span className="text-muted-foreground">
                            {itemConfig?.label || item.name}
                          </span>
                        </div>
                        {item.value && (
                          <span className="font-mono font-medium tabular-nums text-foreground">
                            {item.value.toLocaleString()}
                          </span>
                        )}
                      </div>
                    </>
                  )}
                </div>
              )
            })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload
          .filter((item) => item.type !== "none")
          .map((item) => {
            const key = `${nameKey || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)

            return (
              <div
                key={item.value}
                className={cn(
                  "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
                )}
              >
                {itemConfig?.icon && !hideIcon ? (
                  <itemConfig.icon />
                ) : (
                  <div
                    className="h-2 w-2 shrink-0 rounded-[2px]"
                    style={{
                      backgroundColor: item.color,
                    }}
                  />
                )}
                {itemConfig?.label}
              </div>
            )
          })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

